package com.employee.department_service.controller;import com.employee.department_service.entity.Department;import com.employee.department_service.exception.ResourceNotFoundException;import com.employee.department_service.model.DepartmentDetails;import com.employee.department_service.service.DepartmentService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;import java.util.UUID;import lombok.extern.log4j.Log4j2;@Log4j2@RestController@RequestMapping("/departments")public class DepartmentController {    @Autowired    DepartmentService departmentService;    @GetMapping("/{deptid}")    public Department getDepartment(@PathVariable("deptid") UUID deptid) throws ResourceNotFoundException {        log.info("I am in controller");        return departmentService.getDepartment(deptid);    }    @GetMapping    public ResponseEntity<List<DepartmentDetails>> getAllDepartment(@RequestParam(value = "offset", defaultValue = "0") int offset,                                                                    @RequestParam(value = "count", defaultValue = "5") int count,                                                                    @RequestParam(value = "deptname", required = false) String deptname    )    {        log.info("I am in Department controller");        List<Department> departments = departmentService.getAllDepartment(offset, count, deptname);        List<DepartmentDetails> list = departments.stream().map(department ->                        new DepartmentDetails(department.getDeptid(), department.getDeptname(), department.getHodid()))                .toList();        return new ResponseEntity<List<DepartmentDetails>>(list, HttpStatus.OK);    }}